      SUBROUTINE DSINQMI(N,WSAVE,LENSAV,IER)
      INTEGER N,LENSAV,IER
      DOUBLE PRECISION WSAVE(LENSAV)
C
      IER = 0
C
      IF (LENSAV.LT.2*N+INT(LOG(DBLE(N)))+4) THEN
          IER = 2
          CALL DXERFFT('SINQMI',3)
          GO TO 300
      END IF
C
      CALL DCOSQMI(N,WSAVE,LENSAV,IER1)
      IF (IER1.NE.0) THEN
          IER = 20
          CALL DXERFFT('SINQMI',-5)
      END IF
  300 CONTINUE
      RETURN
      END
